// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import '../pages/Dashboard.css';
import { getTasks, getUserProfile } from '../utils/storage';
import { getProductivityStats, getAIRecommendations } from '../utils/aiEngine';

const Dashboard = () => {
  const [stats, setStats] = useState({
    completed: 0,
    pending: 0,
    productivity: 0,
    focusHours: [],
    sleepCorrelation: [],
  });
  const [recommendations, setRecommendations] = useState([]);
  const [user, setUser] = useState({ name: 'Student', goals: [] });

  useEffect(() => {
    // Fetch user profile and tasks
    setUser(getUserProfile() || { name: 'Student', goals: [] });
    const tasks = getTasks();
    setStats(getProductivityStats(tasks));
    setRecommendations(getAIRecommendations(tasks));
  }, []);

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>Welcome, {user.name}</h2>
        <p>Your goals: {user.goals.length ? user.goals.join(', ') : 'No goals set'}</p>
      </div>

      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>{stats.completed}</h3>
          <p>Tasks Completed</p>
        </div>
        <div className="stat-card">
          <h3>{stats.pending}</h3>
          <p>Pending Tasks</p>
        </div>
        <div className="stat-card">
          <h3>{stats.productivity}%</h3>
          <p>Productivity</p>
        </div>
      </div>

      <div className="dashboard-charts">
        {/* Placeholder: Add chart library such as Recharts, Chart.js, etc. */}
        <div className="chart-placeholder">[Productivity Over Time Chart]</div>
        <div className="chart-placeholder">[Focus Hours Chart]</div>
        <div className="chart-placeholder">[Sleep & Performance Chart]</div>
      </div>

      <div className="dashboard-ai">
        <h3>AI Recommendations</h3>
        {recommendations.length === 0 ? (
          <p>No insights available. Add some tasks!</p>
        ) : (
          <ul>
            {recommendations.map((rec, idx) => (
              <li key={idx}>{rec}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
