import React, { useState, useEffect } from 'react';
import { getFromStorage } from '../utils/storage';
import { analyzeProductivity } from '../utils/aiEngine';
import './Dashboard.css';

function Dashboard() {
  const [stats, setStats] = useState({
    totalTasks: 0,
    completedTasks: 0,
    pendingTasks: 0,
    todayProductivity: 0
  });
  const [recommendations, setRecommendations] = useState([]);
  const [upcomingTasks, setUpcomingTasks] = useState([]);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = () => {
    const tasks = getFromStorage('tasks', []);
    const routineLogs = getFromStorage('routineLogs', []);
    
    const today = new Date().toDateString();
    const todayTasks = tasks.filter(t => 
      new Date(t.deadline).toDateString() === today
    );
    
    const completedTasks = tasks.filter(t => t.completed);
    const pendingTasks = tasks.filter(t => !t.completed);
    
    setStats({
      totalTasks: tasks.length,
      completedTasks: completedTasks.length,
      pendingTasks: pendingTasks.length,
      todayProductivity: calculateTodayProductivity(routineLogs)
    });

    const upcoming = tasks
      .filter(t => !t.completed)
      .sort((a, b) => new Date(a.deadline) - new Date(b.deadline))
      .slice(0, 5);
    
    setUpcomingTasks(upcoming);

    const aiRecommendations = analyzeProductivity(routineLogs, tasks);
    setRecommendations(aiRecommendations.slice(0, 3));
  };

  const calculateTodayProductivity = (logs) => {
    const today = new Date().toDateString();
    const todayLog = logs.find(log => 
      new Date(log.date).toDateString() === today
    );
    
    if (!todayLog) return 0;
    
    const studyHours = todayLog.studyHours || 0;
    const focusScore = studyHours > 0 ? Math.min((studyHours / 8) * 100, 100) : 0;
    return Math.round(focusScore);
  };

  const getTimeUntilDeadline = (deadline) => {
    const now = new Date();
    const deadlineDate = new Date(deadline);
    const diff = deadlineDate - now;
    const hours = Math.floor(diff / (1000 * 60 * 60));
    
    if (hours < 0) return 'Overdue';
    if (hours < 24) return `${hours}h remaining`;
    const days = Math.floor(hours / 24);
    return `${days}d remaining`;
  };

  return (
    <div className="dashboard">
      <div className="page-header">
        <h1 className="page-title">Dashboard</h1>
        <p className="page-subtitle">Your productivity overview</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìù</div>
          <div className="stat-content">
            <div className="stat-value">{stats.totalTasks}</div>
            <div className="stat-label">Total Tasks</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚úÖ</div>
          <div className="stat-content">
            <div className="stat-value">{stats.completedTasks}</div>
            <div className="stat-label">Completed</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚è∞</div>
          <div className="stat-content">
            <div className="stat-value">{stats.pendingTasks}</div>
            <div className="stat-label">Pending</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üéØ</div>
          <div className="stat-content">
            <div className="stat-value">{stats.todayProductivity}%</div>
            <div className="stat-label">Today's Focus</div>
          </div>
        </div>
      </div>

      <div className="dashboard-grid">
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">ü§ñ AI Recommendations</h2>
          </div>
          <div className="recommendations-list">
            {recommendations.length === 0 ? (
              <div className="empty-message">
                Start logging your routine to get personalized recommendations
              </div>
            ) : (
              recommendations.map((rec, index) => (
                <div key={index} className="recommendation-item">
                  <span className="rec-icon">{rec.icon}</span>
                  <div className="rec-content">
                    <div className="rec-text">{rec.text}</div>
                    <div className="rec-time">{rec.timeRange}</div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        <div className="card">
          <div className="card-header">
            <h2 className="card-title">üìÖ Upcoming Tasks</h2>
          </div>
          <div className="tasks-list">
            {upcomingTasks.length === 0 ? (
              <div className="empty-message">
                No pending tasks. Great job! üéâ
              </div>
            ) : (
              upcomingTasks.map(task => (
                <div key={task.id} className="task-item">
                  <div className="task-info">
                    <div className="task-name">{task.name}</div>
                    <span className={`badge badge-${task.priority.toLowerCase()}`}>
                      {task.priority}
                    </span>
                  </div>
                  <div className="task-deadline">
                    {getTimeUntilDeadline(task.deadline)}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
